import React, { useEffect, useState } from "react";
import "../Styles/Home.css";
import { PokemonCard } from "../components/PokemonCard";

export const Home = () => {
  const [offset, setOffset] = useState(0);
  const [pokemons, setPokemons] = useState([]);
  const urlAllPokemons = `https://pokeapi.co/api/v2/pokemon?limit=20 0&offset=${offset}`;

  useEffect(() => {
    const getData = async () => {
      const response = await fetch(urlAllPokemons);
      const data = await response.json();
      setPokemons(data.results);
      console.log(data.results);
    };
    getData();
  }, [urlAllPokemons]);

  function handleNext() {
    setOffset(offset + 20);
  }

  function handlePrev() {
    if (offset >= 20) {
      setOffset(offset - 20);
    }
  }

  function getSprites() {
    let id = 0;
    for (let i = 0; i < pokemons.size(); i++) {
      let url = pokemons[i].url;
      const parts = url.split("/");
      id = parts[parts.length - 2];
    }

    console.log(id);

    return pokemons.map((p) => (
      <img
        src={`https://raw.githubusercontent.com/PokeAPI/sprites/master/sprites/pokemon/shiny/${id}.png`}
        alt={p.name}
        key={p.name}
      />
    ));
  }

  return (
    <div>
      <div className="pokemon-card">{getSprites()}</div>
      <div className="button-container">
        <button onClick={handlePrev}>Previous</button>
        <button onClick={handleNext}>Next</button>
      </div>
    </div>
  );
};
