import React, { useEffect, useState } from 'react'
import '../Styles/Navbar.css';


export const Home = () => {

  const [page,setPage] = useState(1)
  const [pokemonCount, setPokemonCount] = useState(0)


  

  function handleNext() {
    setPage(page + 1);
  }

  function handlePrev() {
    if (page > 1) {
      setPage(page - 1);
    }
  }
 

  return (
    <div>
          <div className="button-container">
        <button
          onClick={handlePrev}
          disabled={page === 1}
          className={page === 1 ? "disabled" : ""}
        >
          Previous
        </button>
        <button
          onClick={handleNext}
          disabled={page * 20 >= pokemonCount}
          className={page * 20 >= pokemonCount ? "disabled" : ""}
        >
          Next
        </button>
      </div>
    </div>
  )
}
