import React, { useEffect, useState } from 'react'
import '../Styles/Home.css';
import { PokemonCard } from '../components/PokemonCard';


export const Home = () => {

  const [offset,setOffset] = useState(0)
  const [pokemons,setPokemons] = useState ([])
  const urlAllPokemons = `https://pokeapi.co/api/v2/pokemon?limit=20&offset=${offset}`


  useEffect(()=>{
    const data = async ()=>{
      await fetch(urlAllPokemons).then((res)=> res.json()).then((data) => setPokemons([...pokemons , ...data.results]))
    }
    data()
  },[offset,pokemons,urlAllPokemons])
  




  function handleNext() {
    setOffset(offset + 20);
  }

  function handlePrev() {
    if (offset >= 20) {
      setOffset(offset - 20);
    }
  }
 

  return (
    <div>
      <div className='pokemon-card'>
        <PokemonCard/>
      </div>
        <div className="button-container">
        <button
          onClick={handlePrev}
        >
          Previous
        </button>
        <button
          onClick={handleNext}
        >
          Next
        </button>
      </div>
    </div>
  )
}
