import React, { useEffect, useState } from "react";
import "../Styles/Home.css";
import { Link } from "react-router-dom";
import { PokemonCard } from "../components/PokemonCard";

export const Home = () => {
  const [offset, setOffset] = useState(0);
  const [pokemons, setPokemons] = useState([]);
  const urlAllPokemons = `https://pokeapi.co/api/v2/pokemon?limit=20 0&offset=${offset}`;

  useEffect(() => {
    const getData = async () => {
      const response = await fetch(urlAllPokemons);
      const data = await response.json();
      setPokemons(data.results);
      console.log(data.results);
    };
    getData();
  }, [urlAllPokemons]);

  function handleNext() {
    setOffset(offset + 20);
  }

  function handlePrev() {
    if (offset >= 20) {
      setOffset(offset - 20);
    }
  }

  function getSprites() {
    return pokemons.map((p) => {
      const url = p.url;
      const parts = url.split("/");
      const id = parts[parts.length - 2]; // Extract the ID from the URL
      const spriteUrl = `https://raw.githubusercontent.com/PokeAPI/sprites/master/sprites/pokemon/shiny/${id}.png`;

      return (
        // Use the Link component directly inside the JSX
        <Link to={`/Pokemon/${id}`} key={id} style={{ textDecoration: "none" }}>
          <img className ="img"
            src={spriteUrl}
            alt={p.name}
            className="pokemon-image"
            style={{ display: "flex", width: "200px", cursor: "pointer" }}
          />
        </Link>
      );
    });
  }

  return (
    <div>
      <div className="img-container">{getSprites()}</div>
      <div className="button-container">
        <button onClick={handlePrev}>Previous</button>
        <button onClick={handleNext}>Next</button>
      </div>
    </div>
  );
};
